package javaprograms;

public class DatatypesDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// in a java program we store the data in a variable
		// variables are temporary memory location where you can store data temporarily
		// how do you write a variable
		// every statement in java ends with ;
		// every varible we have to declare its datatype
		// syntax: datatype variblrname = value
		
		int var1 = 10; // integer
		double var2 = 2.3;  //decimal value
		char var3 = 'A'; //charater
		String var4 = "Selenium"; //string
		boolean var5 = true;  //boolean
		
		
		// write the statement to print the variable valu
		System.out.println("The value of var1:"+ var1);
		System.out.println("The value of var2:"+ var2);
		System.out.println("The value of var3:"+ var3);
		System.out.println("The value of var4:"+ var4);
		System.out.println("The value of var5:"+ var5);
		

	}

}
